import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Filter from './filter' ; 
import ProductContainer from './productContainer';
import './style/product.css' ; 
import { getProducts } from '../fakeServer/productChaillo';
import { paginate } from '../utils/paginate' ; 
import { kebabCase } from 'lodash';

class Products extends Component {
    state = {
        products : getProducts(), 
        pageSize : 8 , 
        currentPage : 1 , 
        sortProduct : { path : 'priceHight' , order : 'asc' },
        selectedBtn : true ,
    }

    buttons = [
        {
            _id : 'asc322ssaf1' , 
            title : 'giảm - tăng',
            path : 'priceLow' , 
            order : 'asc'
        },
        {
            _id : 'asc322ssa31' , 
            title : 'tăng - giảm',
            path : 'priceLow' ,
            order : 'desc' 
        },
        {
            _id : 'asc322ssa21' , 
            title : 'A - Z',
            path : 'title' , 
            order : 'asc'
        },
        {
            _id : 'asc32ss1af1' , 
            title : 'Z - A',
            path : 'title' , 
            order : 'desc' 
        },
        
    ]

    handlePageChange = (page) => {
        this.setState({ currentPage : page }) ; 
    }

    handleSortProduct = (sortProduct) =>{
        console.log(sortProduct) ; 
    }

    render() { 
        const { products , currentPage , pageSize , sortProduct} = this.state ;
        const { length : itemsCount } = products ; 

        const allProduct = paginate(products,currentPage,pageSize) ;
        
        return (
            <div className="product">
                <Filter 
                    sortProduct={sortProduct}
                    buttons={this.buttons}
                />
                <ProductContainer 
                    products={allProduct}
                    itemsCount={itemsCount}
                    currentPage={currentPage}
                    pageSize={pageSize}
                    onPageChange={this.handlePageChange}
                />
            </div>
        );
    }
}
 
export default Products;
