import React, { Component } from 'react';
import _ from "lodash";
// waiting for sort featered in process.... 

class Filter extends Component {
    raiseSort = (button) => {
        const sortProduct = { ...this.props.sortProduct };

        sortProduct._id = button._id ; 
        sortProduct.order = button.order ; 
        sortProduct.path = button.path ; 

        this.props.onSort(sortProduct) ;  
    }

    render() {
        const { buttons } = this.props;

        return (
            <div className='filter'>
                <h6>sắp xếp theo :</h6>
                <div className="wrapper">
                    {buttons.map(button => (
                        <button
                            classname={this.getButtonClass(button)}
                            key={button._id}
                            onClick={() => this.raiseSort(button)}
                        >{ button.title }</button>
                    ))}
                </div>
            </div>
        );
    }

    getButtonClass = (button) => {
        const { sortProduct } = this.props ; 
        return sortProduct[_id] === button._id ? 'active' : '';
    }
}

export default Filter;
