import React, { Component } from 'react';
import ProductContent from './productContent';
import ProductForm from './productForm';
import { getForms } from '../fakeServer/productChaillo';
import { getProducts } from '../fakeServer/productChaillo';
import './style/productDetail.css';
import 'bootstrap/dist/css/bootstrap.min.css';


class ProductDetail extends Component {
    state = {
        forms: [],
        products : [] , 
    }

    componentDidMount() {
        this.setState({
            forms: getForms(),
            products : getProducts() ,
        });
    }

    handleSubmit = (value) =>{
        console.log('you have a new order!',value) ; 
    }

    getProductDetail = (id) => {
        const products = [...this.state.products] ; 
        const product = products.filter(p => p._id === id ) ; 
        console.log(product[0]) ; 
    }

    render() {
        const { match , history } = this.props ;
        const { forms } = this.state ; 
        const product = this.getProductDetail(match.params._id) ; 

        return (
            <React.Fragment>
                <ProductContent 
                    match={match}
                    // product={product}
                />
                <ProductForm 
                    forms={forms} 
                    onSubmit={this.handleSubmit}
                    history={history}       
                />
            </React.Fragment>
        );
    }
}

export default ProductDetail;
