import React, { Component } from 'react';

class Filter extends Component {

    render() {
        const { sortProduct } = this.props ; 

        return (
            <div className='filter'>
                <h6>sắp xếp theo :</h6>
                <div className="wrapper">
                    <button className={ this.displayButton('priceHight')}>giảm - tăng</button>
                    <button>tăng - giảm</button>
                </div>
                <span>|</span>
                <div className='wrapper'>
                    <button className='active'>A - Z</button>
                    <button>Z - A</button>
                </div>
            </div>
        );
    }

    displayButton = (path) =>{
        this.props.sortProduct.path === path ? 'active' : '' ; 
    }
}

export default Filter;
